import React, { useContext, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { FaBars, FaTimes } from "react-icons/fa";

const Navbar = () => {
  const { user, logout, getDecodedToken } = useContext(AuthContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State for burger menu

  const decodedToken = getDecodedToken();
  const isAdmin = decodedToken?.IsAdmin === "true";

  const toggleMenu = () => {
    setIsMenuOpen((prevState) => !prevState); // Toggle burger menu
    console.log("Menu toggled, current state:", isMenuOpen); // Check if the state is changing
  };

  return (
    <nav className="bg-gray-100 p-4 shadow-md">
      <div className="container mx-auto flex items-center justify-between">
        {/* Logo */}
        <div className="text-xl font-bold">
          <Link to="/" className="text-blue-500">
            MyApp
          </Link>
        </div>

        {/* Burger Menu Icon for Small Screens */}
        <div className="md:hidden">
          <button onClick={toggleMenu}>
            {isMenuOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
          </button>
        </div>

        {/* Navigation Links */}
        <ul
          className={`flex flex-col md:flex-row md:space-x-4 md:items-center 
          bg-gray-100 md:bg-transparent w-full md:w-auto md:static absolute right-0 top-full ${
            isMenuOpen ? "block" : "hidden"
          } md:block transition-all duration-300 ease-in-out z-50`}
        >
          <li>
            <NavLink
              to="/"
              className={({ isActive }) =>
                isActive ? "text-red-500" : "text-blue-500"
              }
            >
              Home
            </NavLink>
          </li>
          {user ? (
            <>
              {isAdmin && (
                <li>
                  <NavLink
                    to="/admin-dashboard"
                    className={({ isActive }) =>
                      isActive ? "text-red-500" : "text-blue-500"
                    }
                  >
                    Admin Dashboard
                  </NavLink>
                </li>
              )}
              <li>
                <NavLink
                  to="/user-dashboard"
                  className={({ isActive }) =>
                    isActive ? "text-red-500" : "text-blue-500"
                  }
                >
                  User Dashboard
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/profile"
                  className={({ isActive }) =>
                    isActive ? "text-red-500" : "text-blue-500"
                  }
                >
                  Profile
                </NavLink>
              </li>
              <li>
                <button onClick={logout} className="text-red-500">
                  Log Out
                </button>
              </li>
            </>
          ) : (
            <li>
              <NavLink
                to="/login"
                className={({ isActive }) =>
                  isActive ? "text-red-500" : "text-blue-500"
                }
              >
                Log In
              </NavLink>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
