import React from "react";

const UserSettings = ({ maxBooking, setMaxBooking, allowShowUserInfo, setAllowShowUserInfo }) => {
  return (
    <div className="mt-8 border-t-2 border-black-900">
      <h2 className="font-bold font-xl">Bruger Indstillinger</h2> {/* Translated: User Settings */}
      <div className="flex items-center">
        <input
          type="number"
          value={maxBooking}
          onChange={(e) => setMaxBooking(parseInt(e.target.value, 10))}
          className="m-2 p-2 w-20 border rounded"
        />
        <p className="ml-2">Antal tilladte vasketider per bruger</p> {/* Translated: Max bookings per user */}
      </div>

      <div className="">
        <div className="flex items-center mb-2">
          <input
            id="allowShowUserInfo-checkbox"
            type="checkbox"
            checked={allowShowUserInfo} // Checkbox state controlled by allowShowUserInfo
            onChange={(e) => setAllowShowUserInfo(e.target.checked)} // Update parent state on change
            className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800 focus:ring-2 dark:bg-gray-700 dark:border-gray-600"
          />
          <label htmlFor="allowShowUserInfo-checkbox" className="p-1">
            Tillad visning af brugeres navne {/* Translated: Allow showing user names */}
          </label>
        </div>
      </div>
    </div>
  );
};

export default UserSettings;
